,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'/* prepare all possible snap_id in reports section */',
'declare',
'',
'    l_query varchar2(4000);',
'    l_names      wwv_flow_global.vc_arr2;',
'    l_values     wwv_flow_global.vc_arr2;',
'    ',
'begin',
'      ',
'    l_names(1) := ''DBID'';',
'    l_names(2) := ''P5_INFO_BSNAP'';',
'    l_names(3) := ''P5_INFO_ESNAP'';',
'    l_names(4) := ''P5_INSTANCE_SELECTOR'';',
'    l_names(5) := ''CON_ID'';',
'',
'    l_values(1) := v(''DBID'');',
'    l_values(2) := v(''P5_INFO_BSNAP'');',
'    l_values(3) := v(''P5_INFO_ESNAP'');',
'    l_values(4) := v(''P5_INSTANCE_SELECTOR'');',
'    l_values(5) := v(''CON_ID'');',
'    ',
'    l_query := ''',
'             select ',
'        *',
'        from ',
'        (',
'            select ',
'                snap_id,',
'                to_char(min(END_INTERVAL_TIME),''''MON-DD-YYYY HH24:MI:SS'''') end_interval_time ',
'            from ',
'                dba_hist_snapshot',
'            where',
'                   (',
'                        (dbid = :DBID) AND ',
'                        (snap_id between :P5_INFO_BSNAP and :P5_INFO_ESNAP) AND ',
'                        (((to_char(:P5_INSTANCE_SELECTOR) <> ''''Global'''') and (instance_number = :P5_INSTANCE_SELECTOR)) OR',
'                        ((to_char(:P5_INSTANCE_SELECTOR) = ''''Global'''') and 1=1)) AND',
'                        ((con_id IS NOT NULL and con_id = :CON_ID) OR (con_id IS NULL and 1=1))',
'                   )',
'                   ',
