'',
'declare',
'    bdate date;',
'    edate date;',
'    bsnap number;',
'    esnap number;',
'    INTERVAL_MIN number := 0;',
'begin',
'',
'    bdate := to_date(:P6_BDATE, ''MON-DD-YYYY HH24:MI:SS'');',
'    edate := to_date(:P6_EDATE, ''MON-DD-YYYY HH24:MI:SS'');',
