'    select ',
'        min(snap_id), ',
'        max(snap_id) ',
'    into',
'        bsnap,',
'        esnap',
'    from ',
'        dba_hist_snapshot',
'    where',
'        dbid = :DBID and ',
'        begin_interval_time between bdate and edate',
