'    ',
'    l_query_other := ''',
'        with ',
'        v as ',
'        (',
'            select /*+ MATERIALIZE *//* <Top Activity> get_ash_dimension: query aggregate data which are not in the ash top sql table  */',
'                * ',
'            from',
'            (',
'                select ''|| group_by_clause ||''',
'                FROM ''|| SYS.DBMS_ASSERT.SQL_OBJECT_NAME(active_view_name) ||'' a',
'                where ''|| filter_by_clause ||''',
'                group by ''|| group_by_clause ||''',
'                order by count(*) desc',
'            )',
'            where rownum  <= 15',
'        )',
'        ',
'        select /*+ leading(a) use_hash(b) SWAP_JOIN_INPUTS(b) */',
'            ROUND(RATIO_TO_REPORT(COUNT(*)) OVER () * 100, 2) pct,',
'            to_char(ROUND(''|| to_char(sample_count) ||''*COUNT(*)/''|| to_char(time_range) ||'', 2)) AAS,',
'             ROUND(sum(case when wait_class is null              then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c1 , --"ON CPU"',
'             ROUND(sum(case when wait_class = ''''Other''''          then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c2 , --"Other"',
