'             ROUND(sum(case when wait_class = ''''Configuration''''  then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c4 , --"Configuration"',
'             ROUND(sum(case when wait_class = ''''Cluster''''        then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c5 , --"Cluster"',
'             ROUND(sum(case when wait_class = ''''Administrative'''' then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c6 , --"Administrative"',
'             ROUND(sum(case when wait_class = ''''Concurrency''''    then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c7 , --"Concurrency"',
'             ROUND(sum(case when wait_class = ''''Commit''''         then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c8 , --"Commit"',
'             ROUND(sum(case when wait_class = ''''Network''''        then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c9 , --"Network"',
'             ROUND(sum(case when wait_class = ''''User I/O''''       then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c10, --"User I/O"',
'             ROUND(sum(case when wait_class = ''''System I/O''''     then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c11, --"System I/O"',
'             ROUND(sum(case when wait_class = ''''Scheduler''''      then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c12, --"Scheduler"',
'             ROUND(sum(case when wait_class = ''''Queueing''''       then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c13,  --"Queueing',
'            to_char(count(distinct sql_id)) num, ''''''''',
