'    end if;',
'    ',
'    APEX_COLLECTION.CREATE_COLLECTION_FROM_QUERY(p_collection_name=>''ACTIVITY_TABLE_DATA'',p_query=>l_query);  ',
'    ',
'    /* prepare col_name for select statement in top_event table, under single mode */',
