'            to_number(c002) aas,',
'            ''|| col_name ||''',
'        from',
'            apex_collections',
'        where',
'            collection_name = ''''ACTIVITY_TABLE_DATA''''',
'        order by',
'            to_number(c001),',
'            to_number(c002)',
'        desc',
'    '';',
'',
'    /* ACTIVITY_CUST_TABLE_DATA is the collection for top_event table, under single mode */',
'    if APEX_COLLECTION.COLLECTION_EXISTS(p_collection_name=>''ACTIVITY_CUST_TABLE_DATA'') then ',
'        APEX_COLLECTION.DELETE_COLLECTION (p_collection_name=>''ACTIVITY_CUST_TABLE_DATA'');',
'    end if;',
'    ',
'    APEX_COLLECTION.CREATE_COLLECTION_FROM_QUERY(p_collection_name=>''ACTIVITY_CUST_TABLE_DATA'',p_query=>l_query);  ',
'    ',
'    /* prepare query for top_event table, under compare mode */',
'    l_query := ''',
'        select',
'            to_number(c001) pct,',
'            to_number(c002) aas,',
'            ''|| col_name ||''',
'        from',
'            apex_collections',
'        where',
'            collection_name = ''''ACTIVITY_ALL_EVENT''''',
'        order by',
'            to_number(c001),',
'            to_number(c002)',
'        desc',
'    '';',
'    ',
'    /* ACTIVITY_CUST_ALL_EVENT is the collection for top_event table, under compare mode */',
'    if APEX_COLLECTION.COLLECTION_EXISTS(p_collection_name=>''ACTIVITY_CUST_ALL_EVENT'') then ',
'        APEX_COLLECTION.DELETE_COLLECTION (p_collection_name=>''ACTIVITY_CUST_ALL_EVENT'');',
'    end if;',
'    ',
'    APEX_COLLECTION.CREATE_COLLECTION_FROM_QUERY(p_collection_name=>''ACTIVITY_CUST_ALL_EVENT'',p_query=>l_query);  ',
'    ',
'    ',
'    /* prepare query for top_SQL table */',
'    ',
'    group_by_clause := ''nvl(sql_id, ''''No SQL ID''''),sql_opname'';',
'    ',
'    ',
'    l_query := ''',
'        select /* <Top Activity> get_ash_dimension: query data for ash top sql table and ash top event table */',
'            * ',
'        from',
'        (',
'            select /*+ leading(a) use_hash(b) SWAP_JOIN_INPUTS(b) */',
