'    end if;',
'    ',
'    APEX_COLLECTION.CREATE_COLLECTION_FROM_QUERY(p_collection_name=>''ACTIVITY_ALL_SQL'',p_query=>l_query);',
'',
'    /* ACTIVITY_SQL_TABLE_DATA is the collection for top_SQL table, under single mode */',
'    l_query := l_query || '' where rownum <= 15 '';',
'',
'    if APEX_COLLECTION.COLLECTION_EXISTS(p_collection_name=>''ACTIVITY_SQL_TABLE_DATA'') then ',
'        APEX_COLLECTION.DELETE_COLLECTION (p_collection_name=>''ACTIVITY_SQL_TABLE_DATA'');',
'    end if;',
'    ',
'    APEX_COLLECTION.CREATE_COLLECTION_FROM_QUERY(p_collection_name=>''ACTIVITY_SQL_TABLE_DATA'',p_query=>l_query);  ',
'    ',
'    /* calculate the number of all other SQL in top_SQL table, under single mode */',
'    select SUM(to_number(c001)) pct',
'    into pct_sum',
'    from apex_collections',
'    where collection_name = ''ACTIVITY_SQL_TABLE_DATA'';',
'    ',
'    if pct_sum <= TOP_DIM_PCT then',
'        execute immediate l_query_other into ',
'            aggr_row_pct,aggr_row_aas,aggr_row_c1,aggr_row_c2,aggr_row_c3,aggr_row_c4,aggr_row_c5',
'            ,aggr_row_c6,aggr_row_c7,aggr_row_c8,aggr_row_c9,aggr_row_c10,aggr_row_c11,',
'            aggr_row_c12,aggr_row_c13,aggr_row_num,aggr_row_null;',
'',
'        ',
'        aggr_row_sqlid := ''All other ''|| aggr_row_num ||'' statements'';',
'        APEX_COLLECTION.ADD_MEMBER',
'        (',
'            p_collection_name => ''ACTIVITY_SQL_TABLE_DATA'',',
'            p_c001            => to_char(100 - pct_sum),',
'            p_c002            => aggr_row_aas,',
'            p_c003            => aggr_row_sqlid',
'        );',
'    end if;',
'    ',
'    ',
'    apex_json.open_object;  ',
'    apex_json.write(''success'', true);  ',
'    apex_json.close_object; ',
'end;'))
,p_process_clob_language=>'PLSQL'
);
end;
