 p_id=>wwv_flow_imp.id(78869571403022905)
,p_process_sequence=>20
,p_process_point=>'ON_DEMAND'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'ACTIVITY_TOP_LIST_CALLBACK'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'/* get top list for the dropdown list in top_activity section */',
'declare',
'    bdate date;',
'    edate date;',
'    bsnap number;',
'    esnap number;',
'    ',
'    delta                     number;',
'    time_range                number;',
'    time_range2               number;',
'    points                    number;',
'    sample_count              number := 10;',
'    ',
'    max_date                  varchar2(1000);',
'    ',
'    l_query                   varchar2(4000);',
'    time_str                  varchar2(100) := ''sample_time'';',
'    filter_by_clause          varchar2(1000);',
'    group_by_clause           varchar2(1000);',
'    cap_rep_filter            varchar2(1000) := '' 1 = 1 '';',
'    ',
'    active_view_name          varchar2(100);',
'    sql_plan_view             varchar2(100);',
'    ',
'    limit                     varchar2(5) := ''10'';',
'    activity_by               varchar2(1000) := v(''P15_ACTIVITY_ACTIVITY_BY'');',
'    ',
'begin',
'',
'    ',
'    if :P15_ACTIVITY_ACTIVITY_BY is null then',
'        activity_by := ''nvl(wait_class,''''ON CPU'''')'';',
'    end if;',
'    ',
'    select ',
'        d001, d002, n001, n002',
'    into',
'        bdate, edate, bsnap, esnap',
'    from ',
'        apex_collections',
'    where ',
'        collection_name = ''INFO'';',
'    ',
'    delta := to_number(edate - bdate);',
'   ',
'    filter_by_clause := time_str ||'' between to_timestamp(''''''|| to_char(bdate, ''MM/DD/YYYY HH24:MI:SS'') ||',
'        '''''', ''''MM/DD/YYYY HH24:MI:SS'''') - ''|| to_char(v(''P15_INTERVAL_MIN'')*1/24/60) || '' and '' || '' to_timestamp('''''' || ',
'        to_char(edate, ''MM/DD/YYYY HH24:MI:SS'') || '''''', ''''MM/DD/YYYY HH24:MI:SS'''') and dbid = '' || v(''P15_DBID'');',
'    ',
'    if :P15_INSTANCE_SELECTOR <> ''Global'' then',
'        filter_by_clause := filter_by_clause || '' and instance_number = '' || v(''P15_INSTANCE_SELECTOR'');',
'    end if;',
'    /*',
'    if :VERSION >= ''12'' and :CON_ID is not null then',
'        filter_by_clause := filter_by_clause || '' and con_id = '' || :CON_ID;',
'    end if;',
'    */',
'    if :P15_CAP_REP_FLAG = ''YES'' then',
'        filter_by_clause := filter_by_clause || '' and DBREPLAY_FILE_ID > 0 '';',
'        cap_rep_filter := cap_rep_filter || '' and DBREPLAY_FILE_ID > 0 '';',
'    end if;',
'    ',
'    ',
'    if :P15_LOCAL_CDB = ''YES'' then /*  #local db is CDB*/     ',
'        if :P15_DBID = :P15_LOCAL_DBID and :P15_LOCAL_DBID != :P15_LOCAL_CON_DBID then /* #local pdb info from cdb*/',
'            active_view_name := ''AWR_ROOT_ACTIVE_SESS_HISTORY'';',
'            sql_plan_view := ''awr_root_sql_plan'';',
'            ',
'        else     /*# pdb/ dump in pdb/cdb$root */',
'            active_view_name := ''AWR_PDB_ACTIVE_SESS_HISTORY'';',
'            sql_plan_view := ''awr_pdb_sql_plan'';',
'        end if;',
'    else',
'        active_view_name := ''DBA_HIST_ACTIVE_SESS_HISTORY'';',
'        sql_plan_view := ''dba_hist_sql_plan'';',
'    end if;',
'    ',
'    if activity_by not in (''nvl(wait_class,''''ON CPU'''')'', ''instance_number'', ''inst_id'', ''dbid,con_dbid'') then',
'',
'        l_query := '' ',
'            select /* <Top Activity> query_top_list: query top activites by other filters */',
'                ''|| activity_by ||'' wait_class',
'            FROM',
'            (',
'                select /*+ leading(sh) use_hash(sp) */',
'                    sh.*,',
'                    sp.object_owner,',
'                    sp.object_name',
'                from',
'                (',
'                    select /*+ parallel(4) leading(a) use_hash(b) SWAP_JOIN_INPUTS(b) */',
'                        a.*,',
'                        b.username',
'                    from',
'                        ''|| SYS.DBMS_ASSERT.SQL_OBJECT_NAME(active_view_name) ||'' a,',
'                        dba_users b',
'                    where',
'                        a.user_id = b.user_id(+) and',
'                        ''|| filter_by_clause ||''',
'                ) sh,',
'                ''|| SYS.DBMS_ASSERT.SQL_OBJECT_NAME(sql_plan_view) ||'' sp',
'                    where',
'                        sp.dbid(+) = sh.dbid AND',
'                        sp.sql_id(+) = sh.sql_id AND',
'                        sp.plan_hash_value(+) = sh.sql_plan_hash_value AND',
'                        sp.id(+) = sh.sql_plan_line_id',
'            )',
'            group by ''|| activity_by ||''',
'            order by count(*) desc',
'        '';',
'',
'        if APEX_COLLECTION.COLLECTION_EXISTS (p_collection_name => ''ACTIVITY_TOP_FILTER_LIST'') then',
'                APEX_COLLECTION.DELETE_COLLECTION (p_collection_name => ''ACTIVITY_TOP_FILTER_LIST'');',
'        end if;',
'',
'        APEX_COLLECTION.CREATE_COLLECTION_FROM_QUERY ',
'        (',
'            p_collection_name => ''ACTIVITY_TOP_FILTER_LIST'',',
'            p_query => l_query',
'        );',
'',
'         /* add 15 rows constraint Top Activity chart */',
'        l_query := l_query || '' fetch first '' ||limit|| '' rows only '';   ',
'',
'        if APEX_COLLECTION.COLLECTION_EXISTS (p_collection_name => ''ACTIVITY_TOP_LIST'') then',
'                APEX_COLLECTION.DELETE_COLLECTION (p_collection_name => ''ACTIVITY_TOP_LIST'');',
'        end if;',
'',
'        APEX_COLLECTION.CREATE_COLLECTION_FROM_QUERY ',
'        (',
'            p_collection_name => ''ACTIVITY_TOP_LIST'',',
'            p_query => l_query',
'        );',
'    end if;    ',
'    ',
'    apex_json.open_object;  ',
'    apex_json.write(''success'', true);  ',
'    apex_json.close_object; ',
'end;',
'',
'',
'',
'',
'',
''))
,p_process_clob_language=>'PLSQL'
);
