'        col_name := ''c003 "instance_number", c004 "session_id", c005 "session_serial#", c006 "machine", c007 "program"'';',
'        join_clause := '' ',
'            src1."instance_number" = src2."instance_number" and ',
'            src1."session_id" = src2."session_id" and ',
'            src1."session_serial#" = src2."session_serial#" and ',
