'        ',
'    elsif group_by_clause = ''username'' then',
'        select_clause := '' src1."username" '';',
'        col_name := ''c003 "username"'';',
'        join_clause := '' src1."username" = src2."username" '';',
'        apex_util.set_session_state(''P2_ACTIVITY_EVENT_COL3_CMP'', ''Username'');',
'        ',
'    elsif group_by_clause = ''machine'' then',
