'        join_clause := '' src1."machine" = src2."machine" '';',
'        apex_util.set_session_state(''P2_ACTIVITY_EVENT_COL3_CMP'', ''Machine'');',
'        ',
'    elsif group_by_clause = ''program'' then',
'        select_clause := '' src1."program" '';',
'        col_name := ''c003 "program"'';',
'        join_clause := '' src1."program" = src2."program" '';',
'        apex_util.set_session_state(''P2_ACTIVITY_EVENT_COL3_CMP'', ''Program'');',
'        ',
'    elsif group_by_clause = ''module'' then',
'        select_clause := '' src1."module" '';',
'        col_name := ''c003 "module"'';',
'        join_clause := '' src1."module" = src2."module" '';',
'        apex_util.set_session_state(''P2_ACTIVITY_EVENT_COL3_CMP'', ''Module'');',
'        ',
'    elsif group_by_clause = ''action'' then',
'        select_clause := '' src1."action" '';',
'        col_name := ''c003 "action"'';',
'        join_clause := '' src1."action" = src2."action" '';',
'        apex_util.set_session_state(''P2_ACTIVITY_EVENT_COL3_CMP'', ''Action'');    ',
'        ',
'    elsif group_by_clause = ''sql_plan_hash_value'' then',
'        select_clause := '' src1."sql_plan_hash_value" '';',
'        col_name := ''c003 "sql_plan_hash_value"'';',
'        join_clause := '' src1."sql_plan_hash_value" = src2."sql_plan_hash_value" '';',
'        apex_util.set_session_state(''P2_ACTIVITY_EVENT_COL3_CMP'', ''SQL Plan Hash Value'');',
'        ',
'    elsif group_by_clause = ''sql_plan_line_id,sql_plan_operation'' then',
'        select_clause := '' src1."sql_plan_line_id", src1."sql_plan_operation" '';',
'        col_name := ''c003 "sql_plan_line_id", c004 "sql_plan_operation"'';',
'        join_clause := '' ',
'            src1."sql_plan_line_id" = src2."sql_plan_line_id" and ',
'            src1."sql_plan_operation" = src2."sql_plan_operation" ',
'        '';',
'        apex_util.set_session_state(''P2_ACTIVITY_EVENT_COL3_CMP'', ''SQL Plan Line Id'');',
'        apex_util.set_session_state(''P2_ACTIVITY_EVENT_COL4_CMP'', ''SQL Plan Operation'');',
'        ',
'    else',
'        select_clause := '' src1."nvl(event,''''ON CPU'''')", src1."nvl(wait_class,''''ON CPU'''')" '';',
'        col_name := ''c003 "nvl(event,''''ON CPU'''')", c004 "nvl(wait_class,''''ON CPU'''')"'';',
'        join_clause := '' ',
'            src1."nvl(event,''''ON CPU'''')" = src2."nvl(event,''''ON CPU'''')" and ',
'            src1."nvl(wait_class,''''ON CPU'''')" = src2."nvl(wait_class,''''ON CPU'''')" ',
'        '';',
'        apex_util.set_session_state(''P2_ACTIVITY_EVENT_COL3_CMP'', ''Event Name'');',
'        apex_util.set_session_state(''P2_ACTIVITY_EVENT_COL4_CMP'', ''Wait Class'');',
'    end if;',
'    ',
'    ',
'    /* prepare query for top_event table, under compare mode */',
'    /* the idea behind using left join is: based on src1, find out those event ',
'        with the most difference between src1 and src2 */',
'    l_query := ''',
'        select',
'            abs(nvl(src1.ACTIVITY, 0) - nvl(src2.ACTIVITY, 0)) ACTIVITY,',
'            abs(nvl(src1.aas, 0) - nvl(src2.aas, 0)) aas,',
'            ''|| SYS.DBMS_ASSERT.NOOP(select_clause) ||''',
'        from',
'        (',
'            select',
'                to_number(nvl(c001, 0)) ACTIVITY,',
'                to_number(nvl(c002, 0)) aas,',
'                    ''|| SYS.DBMS_ASSERT.NOOP(col_name) ||''',
'            from',
'                apex_collections',
'            where',
'                collection_name = ''''ACTIVITY_CUST_ALL_EVENT''''',
'        ) src1 left outer join',
'        (',
'            select',
'                to_number(nvl(c001, 0)) ACTIVITY,',
'                to_number(nvl(c002, 0)) aas,',
'                    ''|| SYS.DBMS_ASSERT.NOOP(col_name) ||''',
'            from',
'                apex_collections',
'            where',
'                collection_name = ''''ACTIVITY_CUST_ALL_EVENT_CMP''''',
'        ) src2 on',
'        ',
'            ''|| SYS.DBMS_ASSERT.NOOP(join_clause) ||''',
'        order by ',
'            abs(src1.aas - src2.aas) desc nulls last',
'        fetch next 15 rows only',
'    ',
'    '';',
'    ',
'    /* ACTIVITY_DIFF_EVENT is the collection for top_event table, under compare mode */',
'    if APEX_COLLECTION.COLLECTION_EXISTS(p_collection_name=>''ACTIVITY_DIFF_EVENT'') then ',
'        APEX_COLLECTION.DELETE_COLLECTION (p_collection_name=>''ACTIVITY_DIFF_EVENT'');',
'    end if;',
'    ',
'    APEX_COLLECTION.CREATE_COLLECTION_FROM_QUERY(p_collection_name=>''ACTIVITY_DIFF_EVENT'',p_query=>l_query);  ',
'    ',
'    apex_json.open_object;  ',
'    apex_json.write(''success'', true);  ',
'    apex_json.close_object; ',
'end;'))
,p_process_clob_language=>'PLSQL'
);
end;
