,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'EVENT_DATA_CALLBACK'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'/* prepare data for wait_event section */',
'declare',
'    bdate date;',
'    edate date;',
'    bsnap number;',
'    esnap number;',
'    l_query          varchar2(4000);',
'    time_str         varchar2(4000) := ''begin_interval_time'';',
'    filter           varchar2(4000);',
'    cdb_filter       varchar2(4000);',
'    filter_by_clause varchar2(4000);',
'    dba_hist_view    varchar2(100);',
'    snapshot_view    varchar2(100);',
'begin',
'',
'    select ',
'        d001, d002, n001, n002',
'    into',
'        bdate, edate, bsnap, esnap',
'    from ',
'        apex_collections',
'    where ',
'        collection_name = ''INFO_WAIT_EVENTS'';',
'    ',
'    filter_by_clause := SYS.DBMS_ASSERT.NOOP(time_str)||'' between to_timestamp(''''''|| to_char(bdate, ''MM/DD/YYYY HH24:MI:SS'') ||',
'        '''''', ''''MM/DD/YYYY HH24:MI:SS'''') - ''|| to_char(SYS.DBMS_ASSERT.NOOP(:INTERVAL_MIN)*1/24/60) || '' and '' || '' to_timestamp('''''' || ',
'        to_char(edate, ''MM/DD/YYYY HH24:MI:SS'') || '''''', ''''MM/DD/YYYY HH24:MI:SS'''') and dbid = '' || SYS.DBMS_ASSERT.NOOP(:DBID);',
'    ',
'    if :P7_INSTANCE_SELECTOR <> ''Global'' then',
'        filter_by_clause := SYS.DBMS_ASSERT.NOOP(filter_by_clause) || '' and instance_number ='' || SYS.DBMS_ASSERT.NOOP(:P7_INSTANCE_SELECTOR);',
'    end if;',
'    ',
'    if :VERSION >= ''12'' and :CON_ID is not null then',
'        filter_by_clause := SYS.DBMS_ASSERT.NOOP(filter_by_clause) || '' and con_id = '' || SYS.DBMS_ASSERT.NOOP(:CON_ID);',
'    end if;',
'    ',
'    if :LOCAL_CDB = ''YES'' then ',
'        if :DBID = :LOCAL_DBID and :LOCAL_DBID <> :LOCAL_CON_DBID then',
'            cdb_filter := '' 1=1'';',
'            dba_hist_view := ''awr_root_con_system_event'';',
'            snapshot_view := ''awr_root_snapshot'';',
'        else',
'            cdb_filter := '' 1=1'';',
'            dba_hist_view := ''awr_pdb_system_event'';',
'            snapshot_view := ''awr_pdb_snapshot'';',
'        end if;',
'    else',
'        dba_hist_view := ''dba_hist_system_event'';',
'        snapshot_view := ''dba_hist_snapshot'';',
'        cdb_filter := ''  (s1.con_id = s2.con_id or (s1.con_id is null and s2.con_id is null)) '';',
'    end if;',
'    ',
'    if :P7_INSTANCE_SELECTOR = ''Global'' then',
'        filter := '' dbid = ''|| SYS.DBMS_ASSERT.NOOP(:DBID) ||'' and snap_id between ''|| to_char(SYS.DBMS_ASSERT.NOOP(bsnap)) ||'' and ''|| to_char(SYS.DBMS_ASSERT.NOOP(esnap));',
'        l_query := ''',
'            select /* <Wait Events> query_global: query data for global top 10 wait events of RAC */',
'                event_name, ',
'                max(snap_begin_time) snap_begin_time,  ',
'                max(snap_end_time) snap_end_time, ',
'                sum(waits_per_second) waits_per_second, ',
'                round(sum(delta_wait_time) / decode(sum(delta_waits),0,1,sum(delta_waits)) /1000,1) avg_wait_time, ',
'                snap_id',
'            FROM',
'            (',
'                select ',
'                    event_name, ',
'                    s1.snap_id,',
'                    to_char(s2.last_end_interval_time ,''''mm/dd hh24:mi'''') snap_begin_time,',
'                    to_char(s2.END_INTERVAL_TIME,''''hh24:mi'''') snap_end_time,',
'                    round((TOTAL_WAITS - lag(TOTAL_WAITS) over (partition by s1.instance_number, event_name order by s1.snap_id)) /',
'                        ((round(EXTRACT(DAY FROM s2.END_INTERVAL_TIME - s2.last_end_interval_time ) * 1440',
'                                                      + EXTRACT(HOUR FROM s2.END_INTERVAL_TIME - s2.last_end_interval_time ) * 60',
'                                                      + EXTRACT(MINUTE FROM s2.END_INTERVAL_TIME - s2.last_end_interval_time )',
'                                                      + EXTRACT(SECOND FROM s2.END_INTERVAL_TIME - s2.last_end_interval_time ) / 60, 2))*60),1) waits_per_second,',
'                    (TOTAL_WAITS - lag(TOTAL_WAITS) over (partition by s1.instance_number, event_name order by s1.snap_id)) delta_waits,',
'                    (TIME_WAITED_MICRO - lag(TIME_WAITED_MICRO) over (partition by s1.instance_number, event_name order by s1.snap_id)) delta_wait_time',
'                from ',
'                    (select * from ''|| SYS.DBMS_ASSERT.SQL_OBJECT_NAME(dba_hist_view) ||'' where ''|| filter ||'' ) s1, ',
'                    (',
'                        select a.*, lag(end_interval_time) over (partition by instance_number order by snap_id) as last_end_interval_time ',
'                        from ''|| SYS.DBMS_ASSERT.SQL_OBJECT_NAME(snapshot_view) ||'' a where ''|| SYS.DBMS_ASSERT.NOOP(filter_by_clause) ||''',
'                    ) s2',
'                where ',
'                    s1.dbid = s2.dbid and ',
'                    s1.instance_number = s2.instance_number and ',
'                    s1.snap_id = s2.snap_id and ',
'                    ''|| SYS.DBMS_ASSERT.NOOP(cdb_filter) ||''',
'            )',
'            group by event_name, snap_id',
'            order by event_name, 2'';',
'',
'    else',
'        filter := '' dbid = ''|| SYS.DBMS_ASSERT.NOOP(:DBID) ||'' and snap_id between ''|| to_char(SYS.DBMS_ASSERT.NOOP(bsnap)) ||'' and ''|| to_char(SYS.DBMS_ASSERT.NOOP(esnap)) ||'' and instance_number = ''|| SYS.DBMS_ASSERT.NOOP(:P7_INSTANCE_SELECTOR);',
'        l_query := ''',
'            select /* <Wait events> query: Query data for top 10 wait events on single instance */',
'                event_name,',
'                to_char(s2.last_end_interval_time ,''''mm/dd hh24:mi'''') snap_begin_time,',
'                to_char(s2.END_INTERVAL_TIME,''''hh24:mi'''') snap_end_time,',
'                round((TOTAL_WAITS - lag(TOTAL_WAITS) over (partition by s1.instance_number, event_name order by s1.snap_id)) /',
'                ((round(EXTRACT(DAY FROM s2.END_INTERVAL_TIME - s2.last_end_interval_time ) * 1440',
'                                                  + EXTRACT(HOUR FROM s2.END_INTERVAL_TIME - s2.last_end_interval_time ) * 60',
'                                                  + EXTRACT(MINUTE FROM s2.END_INTERVAL_TIME - s2.last_end_interval_time )',
'                                                  + EXTRACT(SECOND FROM s2.END_INTERVAL_TIME - s2.last_end_interval_time ) / 60, 2))*60),1) waits_per_second,',
'                round((s1.TIME_WAITED_MICRO - lag(TIME_WAITED_MICRO) over (partition by s1.instance_number, event_name order by s1.snap_id)) /',
'                    decode(s1.TOTAL_WAITS - lag(TOTAL_WAITS) over (partition by s1.instance_number, event_name order by s1.snap_id), 0, 1,',
'                    (s1.TOTAL_WAITS - lag(TOTAL_WAITS) over (partition by s1.instance_number, event_name order by s1.snap_id)))/1000,1) as avg_wait_time, ',
'                s1.snap_id',
'            from ',
'                (select * from ''|| SYS.DBMS_ASSERT.SQL_OBJECT_NAME(dba_hist_view) ||'' where ''|| filter ||'' ) s1, ',
'                (',
'                    select a.*, lag(end_interval_time) over (partition by instance_number order by snap_id) as last_end_interval_time ',
'                    from ''|| SYS.DBMS_ASSERT.SQL_OBJECT_NAME(snapshot_view) ||'' a where ''|| SYS.DBMS_ASSERT.NOOP(filter_by_clause) ||''',
'                ) s2',
'            where ',
'                s1.dbid = s2.dbid and ',
'                s1.instance_number = s2.instance_number and ',
'                s1.snap_id = s2.snap_id and ',
'                ''|| SYS.DBMS_ASSERT.NOOP(cdb_filter) ||''',
'            order by event_name, s1.snap_id'';',
'    end if;',
'',
'    if APEX_COLLECTION.COLLECTION_EXISTS (p_collection_name => ''EVENT_DATA'') then',
'            APEX_COLLECTION.DELETE_COLLECTION (p_collection_name => ''EVENT_DATA'');',
'    end if;',
'    ',
'    APEX_COLLECTION.CREATE_COLLECTION_FROM_QUERY ',
'    (',
'        p_collection_name => ''EVENT_DATA'',',
'        p_query => l_query',
'    );',
'',
'    apex_json.open_object;  ',
'    apex_json.write(''success'', true);  ',
'    apex_json.close_object; ',
'end;'))
