,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_query varchar2(4000);',
'begin',
'    /* current concept of join: join two src with the same sql_id and sql_opname,',
'        which means that only the sql show in both src1 and src2 will appear in ',
'        this table. Null case(sql only exists in one of the src) are not covered.',
'        Maybe it would be more meaningful to use left join, just like what I do',
'        in TOP_EVENT_DIFF. */',
'        ',
'        l_query := ''',
'            select',
'                src1."nvl(sql_id, ''''No SQL ID'''')" "sql_id_cmp",',
'                src1."sql_opname" "sql_opname_cmp",',
'                src1.ACTIVITY "db1_activity_cmp",',
'                src1.aas "db1_aas_cmp",',
'                src2.ACTIVITY "db2_activity_cmp",',
'                src2.aas "db2_aas_cmp",',
'                abs(src1.ACTIVITY - src2.ACTIVITY) "diff_of_activity_cmp"',
'            from',
'            (',
'                select',
'                    to_number(c001) ACTIVITY,',
'                    to_number(c002) aas,',
'                    nvl(c003, ''''No SQL ID'''') "nvl(sql_id, ''''No SQL ID'''')",',
'                    c004 "sql_opname"',
'                from',
'                    apex_collections',
'                where',
'                    collection_name = ''''ACTIVITY_ALL_SQL''''',
'            ) src1,',
'            (',
'                select',
'                    to_number(c001) ACTIVITY,',
'                    to_number(c002) aas,',
'                    nvl(c003, ''''No SQL ID'''') "nvl(sql_id, ''''No SQL ID'''')",',
'                    c004 "sql_opname"',
'                from',
'                    apex_collections',
'                where',
'                    collection_name = ''''ACTIVITY_ALL_SQL_CMP''''',
'            ) src2',
'            where',
'                src1."nvl(sql_id, ''''No SQL ID'''')" = src2."nvl(sql_id, ''''No SQL ID'''')" and',
'                src1."sql_opname" = src2."sql_opname"',
'            order by abs(src1.aas - src2.aas) desc',
'            fetch next 15 rows only',
'        '';',
'    ',
'    return l_query;',
'end;'))
