'    /* get basic data for top sql table and top event table */',
'    l_query := ''',
'        select /* <Top Activity> get_ash_dimension: query data for ash top sql table and ash top event table */',
'            * ',
'        from',
'        (',
'            select /*+ leading(a) use_hash(b) SWAP_JOIN_INPUTS(b) */',
'                ROUND(RATIO_TO_REPORT(COUNT(*)) OVER () * 100, 2) pct,',
'                TO_CHAR(ROUND(''|| to_char(sample_count) ||''*COUNT(*)/''|| to_char(time_range) ||'', 2)) AAS,',
'                ROUND(sum(case when wait_class is null            then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c1 , ',
'                ROUND(sum(case when wait_class = ''''Other''''          then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c2 , --"Other"',
'                ROUND(sum(case when wait_class = ''''Application''''    then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c3 , --"Application"',
'                ROUND(sum(case when wait_class = ''''Configuration''''  then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c4 , --"Configuration"',
'                ROUND(sum(case when wait_class = ''''Cluster''''        then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c5 , --"Cluster"',
