'                ROUND(sum(case when wait_class = ''''Commit''''         then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c8 , --"Commit"',
'                ROUND(sum(case when wait_class = ''''Network''''        then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c9 , --"Network"',
'                ROUND(sum(case when wait_class = ''''User I/O''''       then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c10, --"User I/O"',
'                ROUND(sum(case when wait_class = ''''System I/O''''     then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c11, --"System I/O"',
'                ROUND(sum(case when wait_class = ''''Scheduler''''      then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c12, --"Scheduler"',
'                ROUND(sum(case when wait_class = ''''Queueing''''       then 1 else 0 end)/count(*) * (RATIO_TO_REPORT(COUNT(*)) OVER () * 100),2) as c13,  --"Queueing',
'                ''|| group_by_clause ||''',
'            FROM ',
'                ''|| active_view_name ||'' a, ',
'                dba_users b',
'            where',
'                a.user_id = b.user_id(+) and',
'                ''|| filter_by_clause ||''',
'            group by ''|| group_by_clause ||''',
'            order by count(*) desc',
'        )',
'    '';',
'    ',
'    /* prepare data for compare mode, with all data rather than only 15 rows */',
'    if APEX_COLLECTION.COLLECTION_EXISTS(p_collection_name=>''ACTIVITY_ALL_EVENT'') then ',
'        APEX_COLLECTION.DELETE_COLLECTION (p_collection_name=>''ACTIVITY_ALL_EVENT'');',
'    end if;',
'    ',
'    APEX_COLLECTION.CREATE_COLLECTION_FROM_QUERY(p_collection_name=>''ACTIVITY_ALL_EVENT'',p_query=>l_query);',
'',
'    /* add 15 rows constraint for single mode */',
'    l_query := l_query || '' where rownum <= 15 '';',
'    ',
'    group_by_clause :=  ''nvl(sql_id, ''''No SQL ID''''),sql_opname'';',
'    ',
'    l_query_other := ''',
'        with ',
'        v as ',
'        (',
'            select /*+ MATERIALIZE *//* <Top Activity> get_ash_dimension: query aggregate data which are not in the ash top sql table  */',
'                * ',
