'        time_range2 := 3600;',
'        points := round(delta*24);',
'        group_by_clause := ''trunc(sample_time, ''''HH24'''')'';',
'        max_date := ''trunc(to_timestamp(''''''|| to_char(edate, ''yyyymmddhh24miss'') ||'''''',''''yyyymmddhh24miss''''),''''HH24'''')'';',
'    end if;',
'   ',
'    filter_by_clause := time_str||'' between to_timestamp(''''''|| to_char(bdate, ''MM/DD/YYYY HH24:MI:SS'') ||',
