,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'ACTIVITY_TABLE_DATA_CALLBACK'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'/* prepare data for top_sql and top_event table in top_activity section */',
'declare',
'    bdate date;',
'    edate date;',
'    bsnap number;',
'    esnap number;',
'    ',
'    delta             number;',
'    time_range        number;',
'    sample_count      number := 10;',
'    ',
