'    aggr_row_c2       number;',
'    aggr_row_c3       number;',
'    aggr_row_c4       number;',
'    aggr_row_c5       number;',
'    aggr_row_c6       number;',
'    aggr_row_c7       number;',
'    aggr_row_c8       number;',
'    aggr_row_c9       number;',
'    aggr_row_c10       number;',
'    aggr_row_c11       number;',
'    aggr_row_c12       number;',
'    aggr_row_c13       number;',
'begin',
'',
'    if group_by_clause is null then',
'        group_by_clause := ''nvl(event,''''ON CPU''''),nvl(wait_class,''''ON CPU'''')'';',
'    end if;',
'',
'    select ',
'        d001, d002, n001, n002',
'    into',
'        bdate, edate, bsnap, esnap',
'    from ',
'        apex_collections',
'    where ',
'        collection_name = ''INFO'';',
'    ',
'    delta := edate - bdate;',
'    time_range := round(to_number(delta)*24*3600);',
'    ',
'    if :P8_VIEWPORT_SDATE is not null and :P8_VIEWPORT_EDATE is not null then ',
'         filter_by_clause := time_str||'' between to_timestamp(''''''|| SYS.DBMS_ASSERT.NOOP(:P8_VIEWPORT_SDATE) ||',
'        '''''', ''''MM/DD/YYYY HH24:MI:SS'''') - ''|| to_char(SYS.DBMS_ASSERT.NOOP(:INTERVAL_MIN)*1/24/60) || '' and '' || '' to_timestamp('''''' || ',
'        SYS.DBMS_ASSERT.NOOP(:P8_VIEWPORT_EDATE) || '''''', ''''MM/DD/YYYY HH24:MI:SS'''') and dbid = '' || SYS.DBMS_ASSERT.NOOP(:DBID);',
