'                a.user_id = b.user_id(+) and',
'                ''|| filter_by_clause ||''',
'            group by ''|| group_by_clause ||''',
'            order by count(*) desc',
'        )',
'    '';',
'    ',
'    /* prepare data for compare mode, with all data rather than only 15 rows */',
'    if APEX_COLLECTION.COLLECTION_EXISTS(p_collection_name=>''ACTIVITY_ALL_EVENT'') then ',
'        APEX_COLLECTION.DELETE_COLLECTION (p_collection_name=>''ACTIVITY_ALL_EVENT'');',
'    end if;',
'    ',
'    APEX_COLLECTION.CREATE_COLLECTION_FROM_QUERY(p_collection_name=>''ACTIVITY_ALL_EVENT'',p_query=>l_query);',
'',
'    /* add 15 rows constraint for single mode */',
'    l_query := l_query || '' where rownum <= 15 '';',
'    ',
'    group_by_clause :=  ''nvl(sql_id, ''''No SQL ID''''),sql_opname'';',
'    ',
'    l_query_other := ''',
'        with ',
'        v as ',
'        (',
'            select /*+ MATERIALIZE *//* <Top Activity> get_ash_dimension: query aggregate data which are not in the ash top sql table  */',
'                * ',
'            from',
'            (',
'                select ''|| group_by_clause ||''',
